$(".tooltip-parent").mouseleave(function () {
    $(this).removeClass("_show")
})

$(document).mouseup( function(e){
    let div = $( ".tooltip-parent" );
    if ( !div.is(e.target)
        && div.has(e.target).length === 0 ) {
        if($(div).hasClass("_show")){
            $(div).removeClass("_show")
        }
    }
});

