function Sliders() {
    let SlidersArray = []

    let sliders = document.querySelectorAll(".slider")
    sliders.forEach((slider,index) => {
        slider.setAttribute("data-slider-id",index)
        let isLoop = false
        if(slider.classList.contains("_loop")){
            isLoop = true
        }

        SlidersArray[index] = new Swiper(`.slider[data-slider-id="${index}"] .swiper-container`, {
            slidesPerView: "auto",
            spaceBetween: 0,
            watchSlidesProgress: true,
            preventClicks :true,
            a11y: false,
            observer: true,
            observeParents: true,
            observeSlideChildren: true,
            loop: isLoop,
            navigation: {
                nextEl: `.slider[data-slider-id="${index}"] .swiper-button-next`,
                prevEl: `.slider[data-slider-id="${index}"] .swiper-button-prev`,
            },
            pagination: {
                el: `.slider[data-slider-id="${index}"] .swiper-pagination`,
                type: 'bullets',
                clickable: true,
            },
        })

        function updateCounter(sw) {
            const el = slider.querySelector(".swiper-counter");
            if (!el) return;

            // В loop режимах используем realIndex (0-based), total берём из реальных слайдов
            const current = (sw.realIndex ?? sw.activeIndex) + 1;
            const total = sw.slides.length - (sw.params.loop ? sw.loopedSlides * 2 : 0);

            el.innerHTML = `
                <span class="counter-current">${current}</span>
                <span class="counter-dash">—</span>
                <span class="counter-total">${total}</span>
              `;
        }

        updateCounter(SlidersArray[index]);
        SlidersArray[index].on("slideChange", () => updateCounter(SlidersArray[index]));
        SlidersArray[index].on("update", () => updateCounter(SlidersArray[index]));
    })
}

